import React from 'react'
import styled from 'styled-components'

import { ReactComponent as Tab } from '../../src/assets/svg/intersect.svg'

import { FiHome, FiUsers, FiUser } from 'react-icons/fi'

export const ContainerTab = styled.span`
  z-index: 99;
  position: fixed;
  bottom: 0;
  display: flex;
  align-items: center;
  width: 100%;
`

export const TabBar = styled(Tab)`
  width: 100%;
`

export const ContainerButtons = styled.span`
  position: relative;
  height: 0;
  width: 0;
`
export const ContainerVirtualButtons = styled.span`
  position: absolute;
  height: fit-content;
  width: 100vw;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  color: ${(props) => props.theme.colors.background.secondary.two} !important;
`
export const Button = styled.span`
  height: 3rem;
  width: 3rem;
  z-index: 2;
  color: white;
  padding: 0.5rem;
  border-radius: 50%;
  display: flex;
  align-items: center;
  position: relative;
  top: -2rem;
  background: linear-gradient(00deg, #50fa7b 0%, #00d58f 100%);
  box-shadow: 4px 4px 10px rgba(80, 46, 252, 0.14),
    -4px -4px 10px ${(props) => props.theme.colors.primary.one + '26'};
`

export const IconHome = styled(FiHome)`
  height: auto;
  width: 1rem;
  z-index: 2;
  margin: auto;
`

export const IconUser = styled(FiUser)`
  height: auto;
  width: 1rem;
  display: inline-block;
  margin: auto;
  z-index: 5;
`
export const IconTeam = styled(FiUsers)`
  height: auto;
  width: 1rem;
  display: inline-block;
  margin: auto;
  z-index: 5;
`
