import React from 'react'
import styled from 'styled-components'

import svg from '../../src/assets/svg/subtract.svg'

import { FiChevronsDown, FiPlus } from 'react-icons/fi'

export const ContainerTab = styled.span`
  width: 100vw;
  height: ${(props) => props.theme.navigation.height};
  background: white;
  z-index: 9999;
  position: fixed;
  bottom: 0;
`
export const TabBar = styled.span`
  position: relative;
  height: 0;
  width: 0;
`
export const BoxRadiusLeft = styled.span`
  position: absolute;
  height: 10rem;
  width: auto;
  top: -50px;
`
export const BoxRadiusRight = styled.span`
  position: relative;
  height: 0;
  width: 0;
`
