import React from 'react'
import styled from 'styled-components'

import { ReactComponent as Tab } from '../../src/assets/svg/navigator.svg'

export const ContainerTab = styled.span`
  z-index: 9999;
  position: fixed;
  bottom: 0;
  display: flex;
  align-items: center;
  width: 100%;
`

export const TabBar = styled(Tab)`
  height: ${(props) => props.theme.navigation.height};
  z-index: 5;
  position: fixed;
  bottom: -0.2rem;
  margin: 0;
  left: 0px;
  background-size: cover;
  padding: 0; /* reset */
  & path {
    fill: white;
    width: 101%;
  }
`
