import React from 'react'
import styled from 'styled-components'

import svg from '../../src/assets/svg/subtract.svg'

import { FiChevronsDown, FiPlus } from 'react-icons/fi'

export const TabBar = styled.span`
  color: ${(props) => props.theme.colors.primary.one};
  width: 100vw;
  height: ${(props) => props.theme.navigation.height};
  background: white;
  z-index: 9999;
  position: fixed;
  bottom: 0;
  /* clip-path: url('../../src/assets/svg/subtract.svg'); */
  right: 0;
  background-image: linear-gradient(to right, transparent 210px, #b0102d 210px);
  margin-top: 15vh;
  clip-path: polygon(100% 0%, 100% 100%, 25% 100%, 0 50%, 25% 0);

  &::before {
    content: '';
    background-color: #b0102d;
    clip-path: ellipse(200px 45px at 210px);
  }
`
