import React, { useState, useEffect } from 'react'

import { IButtonsAnimate } from '../../src/interfaces'

import {
  Button,
  IconHome,
  IconUsers,
  IconTeam,
  ContainerTab,
  TabBar,
  ContainerButtons,
  ContainerVirtualButtons,
  IconVip,
  IconSign,
} from '../../styles/components/Navigation'

export const Navigation = () => {
  const [animate, setAnimate] = useState<IButtonsAnimate>({
    vip: false,
    team: false,
    home: false,
    users: false,
    sign: false,
  })

  const [screen, setScreen] = useState<IButtonsAnimate>({
    vip: false,
    team: false,
    home: false,
    users: false,
    sign: false,
  })

  useEffect(() => {
    console.log(animate)
  }, [animate])

  const buttonAnimate = (nameButton) => {
    if (!animate[nameButton]) {
      setAnimate((state) => {
        const stateFalsies = Object.keys(state).forEach((v) => (state[v] = false))
        const updatedState = { ...stateFalsies, [nameButton]: true }
        return updatedState
      })
    }
  }

  // ({ ...state, [nameButton]: true }))
  // setTimeout(() => {
  //   setAnimate((state) => ({ ...state, [nameButton]: false }))
  // }, 1000)

  return (
    <>
      <ContainerTab>
        <ContainerButtons>
          <ContainerVirtualButtons>
            <Button animate={animate.vip} onClick={() => buttonAnimate('vip')}>
              <IconVip />
            </Button>
            <Button animate={animate.team} onClick={() => buttonAnimate('team')}>
              <IconTeam />
            </Button>
            <Button animate={animate.home} onClick={() => buttonAnimate('home')}>
              <IconHome />
            </Button>
            <Button animate={animate.users} onClick={() => buttonAnimate('users')}>
              <IconUsers />
            </Button>
            <Button animate={animate.sign} onClick={() => buttonAnimate('sign')}>
              <IconSign />
            </Button>
          </ContainerVirtualButtons>
        </ContainerButtons>
        <TabBar />
      </ContainerTab>
    </>
  )
}
