import React from 'react'
import styled from 'styled-components'

import { ReactComponent as Tab } from '../../src/assets/svg/intersect.svg'

import { FiHome, FiUsers, FiUser } from 'react-icons/fi'

export const ContainerTab = styled.span`
  z-index: 9999;
  position: fixed;
  bottom: 0;
  display: flex;
  align-items: center;
  width: 100%;
`

export const TabBar = styled(Tab)`
  width: 100%;
`

export const ContainerButtons = styled(Tab)`
  position: relative;
  height: 0;
  width: 0;
`

export const ButtonHome = styled(FiHome)`
  height: auto;
  width: 1rem;
  z-index: 99999999999;
  color: red;
`

export const ButtonUser = styled(FiUser)`
  height: auto;
  width: 1rem;
  display: inline-block;
  margin: auto;
  z-index: 5;
  position: relative;
`
export const ButtonTeam = styled(FiUsers)`
  height: auto;
  width: 1rem;
  display: inline-block;
  margin: auto;
  z-index: 5;
`
